import React, { FC } from 'react';
import { withRouter, RouteProps } from 'react-router-dom';
import './index.less';

export interface IHomeButtonProps extends RouteProps {}

export const HomeButton = withRouter(({ location }: IHomeButtonProps) => {
  return (
    <div
      className={[
        'home-router-button',
        location?.pathname === '/' ? 'actived' : '',
      ].join(' ')}
    >
      home
    </div>
  );
});

export interface IFooterBarProps {}

const FooterBar: FC<IFooterBarProps> = (props) => {
  return (
    <div className="footer-bar-wrapper">
      <HomeButton />
    </div>
  );
};

export default FooterBar;
